import React from 'react';
import emailIcon from './pic/email_icon.png';
import userIcon from './pic/password_icon.png';
import phoneIcon from './pic/visible_icon.png';
import genderIcon from './pic/invisible_icon.png';
import errorIcon from './pic/error_icon.png';

class SignUp_customer_1 extends React.Component {
    constructor()
    {
        super();
        this.state = { 
            username: "",
            name: "",
            surname: "",
            email: "",
            phone : "",
            usernameError: "",
            nameError: "",
            surnameError: "",
            emailError: "",
            phoneError: ""
        }
    }
    
    // validate = () => {
    //     let usernameError=""
    //     let nameError=""
    //     let surnameError=""
    //     let emailError=""
    //     let phoneError=""
        
    //     if (!this.state.email.includes("@")) {
    //         emailError = "invalid email !";
    //     }
    //     if (emailError || passwordError) {
    //         this.setState({ emailError, passwordError });
    //         return false;
    //     }

    //     return true;
    // };

    // handleChange = event => {
    //     // event.preventDefault();
    //     this.setState({[event.target.id]: event.target.value});
    // };

    // handleSubmit = event => {
    //     event.preventDefault();
    //     const isValid = this.validate();
    //     if (isValid) {
    //       console.log(this.state);
    //       this.setState(this.state);
    //       alert("Welcome to AHair");
    //     }
    // };

    render(){
        return(
            <div className="big_container">
                <div className="container_signup">
                    <div className="wrap_signup">
                        <div className="signup_form">

                            <span className="signup_form_title">
                                <h1 style={{color:"#CB2D6F",fontSize:"50px"}}>
                                    Sign in
                                </h1>
                                <h5 style={{color:"#CB2D6F"}}>
                                    Page 1 of 2
                                </h5>
                            </span>
        
                            <div className="wrap_input">
                                <img className="input_icon"src={userIcon} alt=""/>
                                <input  
                                    className = "input" 
                                    type = "text"
                                    id = "username"
                                    placeholder = "Username"
                                    value = {this.state.username}  
                                    onChange = {this.handleChange} 
                                />
                                <div className={this.state.usernameError===""? "validate_wrap" :"invalidate_wrap"}>
                                    <div className="erroricon">
                                        <img src={errorIcon} alt= "" width="20px" />
                                    </div>
                                    <div className="texterror">
                                        <span>{this.state.usernameError}</span>
                                    </div>
                                </div>
                            </div>

                            <div className="wrap_input">
                                <img className="input_icon"src={userIcon} alt=""/>
                                <input  
                                    className = "input" 
                                    type = "text"
                                    id = "name"
                                    placeholder = "Name"
                                    value = {this.state.name}  
                                    onChange = {this.handleChange} 
                                />
                                <div className={this.state.nameError===""? "validate_wrap" :"invalidate_wrap"}>
                                    <div className="erroricon">
                                        <img src={errorIcon} alt= "" width="20px" />
                                    </div>
                                    <div className="texterror">
                                        <span>{this.state.usernameError}</span>
                                    </div>
                                </div>
                            </div>

                            <div className="wrap_input">
                                <img className="input_icon"src={userIcon} alt=""/>
                                <input  
                                    className = "input" 
                                    type = "text"
                                    id = "surname"
                                    placeholder = "Surname"
                                    value = {this.state.surname}  
                                    onChange = {this.handleChange} 
                                />
                                <div className={this.state.surnameError===""? "validate_wrap" :"invalidate_wrap"}>
                                    <div className="erroricon">
                                        <img src={errorIcon} alt= "" width="20px" />
                                    </div>
                                    <div className="texterror">
                                        <span>{this.state.surnameError}</span>
                                    </div>
                                </div>
                            </div>
                            <div className="wrap_input">
                                <img className="input_icon"src={emailIcon} alt=""/>
                                <input  
                                    className = "input" 
                                    type = "email"
                                    id = "email"
                                    placeholder = "Email"
                                    value = {this.state.email}  
                                    onChange = {this.handleChange} 
                                />
                                <div className={this.state.emailError===""? "validate_wrap" :"invalidate_wrap"}>
                                    <div className="erroricon">
                                        <img src={errorIcon} alt= "" width="20px" />
                                    </div>
                                    <div className="texterror">
                                        <span>{this.state.emailError}</span>
                                    </div>
                                </div>
                            </div>

                            <div className="wrap_input">
                                <img className="input_icon"src={phoneIcon} alt=""/>
                                <input  
                                    className = "input" 
                                    type = "tel"
                                    pattern="[0-9]{10}"
                                    id = "phone"
                                    placeholder = "Phone number"
                                    value = {this.state.phone}  
                                    onChange = {this.handleChange} 
                                />
                                <div className={this.state.phoneError===""? "validate_wrap" :"invalidate_wrap"}>
                                    <div className="erroricon">
                                        <img src={errorIcon} alt= "" width="20px" />
                                    </div>
                                    <div className="texterror">
                                        <span>{this.state.phoneError}</span>
                                    </div>
                                </div>
                            </div>

                            <div className="wrap_input">
                                <img className="input_icon" src={genderIcon} alt=""/>
                                <select className="input" id="gender">
                                    <option value="agender">Agender</option>
                                    <option value="female">Female</option>
                                    <option value="male">Male</option>
                                </select>
                            </div>
        
                            <div className="container_next_bt">
                                <div>
                                    <button className="login_button">
                                        Back
                                    </button>
                                </div>
                                <form onSubmit={this.handleSubmit}>
                                    <button className="login_button" >
                                        Next
                                    </button>
                                </form>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default SignUp_customer_1;
