import React from 'react'

class MultipleImageUpload extends React.Component {

    imageFileArray = [];
    imagePreviewArray = [];
    imageUrlArray =[];

    constructor(){
        super();
        this.state = {
            imageFile: [],
            imagePreview: [],
            imageUrl: [],
            isSaved: false
        }
    }
    
    hideSaved = () => {
        this.setState({ isSaved: false})
    }

    handleSubmit = event => {
        event.preventDefault()
        console.log(this.state)
        // if(!this.state.isSaved){
        //     this.props.getFile(this.state.imageFile, this.state.imagePreview, this.state.imageUrl )
        // }
    }

    handleChange = event => {
        event.preventDefault();

        let reader = new FileReader();
        let file = event.target.files[0];
        

        reader.onloadend = () => {
            this.state.imageFile.push(file);
            this.state.imagePreview.push(reader.result);
            this.state.imageUrl.push(URL.createObjectURL(file));
            // this.setState({
            //     imageFile: file, imagePreview: reader.result, imageUrl: URL.createObjectURL(file)
            // });
        }
        this.setState({ isSaved: true })
        if (file && file.type.match('image.*')) {
            reader.readAsDataURL(file);
        }
        console.log(this.state)

        // this.imageObject.push(event.target.files)
        // for (let i = 0; i < this.imageObject[0].length; i++) {
        //     this.imageArray.push(URL.createObjectURL(this.fileObj[0][i]))
        // }
        // this.setState({ file: this.fileArray })
    }

    render(){
        return(
            <div className = "container_profile">
                
                <div className = "wrap_preview">
                    {/* {(this.state.imagePreview || []).map(file => (
                        <img src={file} alt="..." />
                    ))} */}
                    <img 
                        className = "image_preview"
                        alt = ""
                        src = {this.state.imagePreview.length}
                    />
                </div>

                <form onSubmit={this.handleSubmit}>
                    <input 
                        className = "file_input"
                        type = "file"
                        id = "image_file"
                        onChange = {this.handleChange}
                    />
                   <div className="wrap_button">
                        <button className={this.state.isSaved? "showsave_button": "hidsave_button"} type="submit" onClick={this.hideSaved}>
                            Save
                        </button>
                    </div>
                </form>

            </div>
        )
    }
}

export default MultipleImageUpload;
