import React from 'react';
import emailIcon from './pic/email_icon.png';
import passwordIcon from './pic/password_icon.png';
import visibleIcon from './pic/visible_icon.png';
import invisibleIcon from './pic/invisible_icon.png';


class Login extends React.Component {
    constructor()
    {
        super();
        this.state = { hidePassword: true }
    }
    managePasswordVisibility = () =>
    {
        this.setState({ hidePassword: !this.state.hidePassword });
    }

    componentDidUpdate(){
        console.log(this.state.hidePassword)
    }

    render(){
        return(
            <div className = "loginForm">
                <form>
                    <h1 className="title">Sign in</h1>
                    <h5 className="title">to continuous your service</h5>

                    <div className="input-div" data-validate="Enteremail">
                        <img src={emailIcon} width="35" />
                        <input className="input"type="email" placeholder="Email" />
                        <span class="focus-input"></span>
                    </div>

                    <div className="input-div" data-validate="Enterpassword">
                        <img src={passwordIcon} width="35" />
                        <input className="input"type={this.state.hidePassword? "password": "text"} placeholder="Password" />
                        <img onClick={this.managePasswordVisibility} src={this.state.hidePassword? visibleIcon: invisibleIcon} width="30"/>
                        <span class="focus-input"></span>
                        
                    </div>

                    <div className="button-div">
                        <button className="button" type="submit">Sing in</button>
                    </div>

                    <h5 style={{color: "white"}}>Don't have an account ? <a href="test.js"> Sing up</a></h5> 
                </form>
            </div>
        );
    }
}
export default Login;
