import React from 'react'
import userImage from './pic/user_green_icon.png'

class ImageUpload extends React.Component {
    constructor(){
        super();
        this.state = {
            imageFile: {userImage},
            imagePreview: ""
        }
    }
    
    handleSubmit = event => {
        event.preventDefault();
        console.log(this.state);
        this.setState(this.state);
    }

    handleChange = event => {
        event.preventDefault();

        let reader = new FileReader();
        let file = event.target.files[0];

        reader.onloadend = () => {
            this.setState({
                imageFile: file, imagePreview: reader.result
            });
        }
        reader.readAsDataURL(file);
    }

    render(){
        return(
            <div className = "container_profile">
                
                <form onSubmit={this.handleSubmit}>
                    <input 
                        className = "file_input"
                        type = "file"
                        id = "image_file"
                        onChange = {this.handleChange}
                    />
                    <button type="submit"onClick={this.handleSubmit}>upload</button>
                </form>
                <div className = "wrap_preview">
                    <img 
                        className = "image_preview"
                        alt = ""
                        src = {this.state.imagePreview? this.state.imagePreview : userImage }
                    />
                </div>
            </div>
        )
    }
}

export default ImageUpload;
