import React from "react";

export const CheckBox = props => {
    return (
        <div>
            <input 
                key = {props.id} 
                onClick = {props.handleCheckChieldElement} 
                type = "checkbox" 
                checked = {props.isChecked} 
                value = {props.value} 
                onChange={()=>{}}
            /> {props.value}
        </div>
    )
}

class Hairstyles extends React.Component {
    constructor() {
        super()
        this.state = {
            womenShort: [],
            womenMedium: [],
            womenLong: [],
            menShort: [],
            menLong: [],

            list_womenShort: [
                {id: "ws1", value: "ws1",  key: 1, isChecked: false},
                {id: "ws2", value: "ws2",  key: 2, isChecked: false},
                {id: "ws3", value: "ws3",  key: 3, isChecked: false},
                {id: "ws4", value: "ws4",  key: 4, isChecked: false}
            ]
        }
    }
    
    handleAllChecked = event => {
        this.state.list_womenShort.forEach(list_womenShort => list_womenShort.isChecked = event.target.checked) 
        this.setState({list_womenShort: this.state.list_womenShort})
    }

    handleChecked = event => {
        this.state.list_womenShort.forEach(list_womenShort => {
        if (list_womenShort.value === event.target.value)
            list_womenShort.isChecked =  event.target.checked
        })
        this.setState({list_womenShort: this.state.list_womenShort})
        // console.log(this.state)
    }
    handleSubmit = event => {
        event.preventDefault();
        this.state.list_womenShort.forEach(list_womenShort => {
            if(list_womenShort.isChecked){
                this.state.womenShort.push(list_womenShort.value)
            }
        })
        console.log(this.state)
    }

    render() {
        return (
        <div >
            <input 
                type="checkbox" 
                value="checkedall"
                onClick={this.handleAllChecked}   
                onChange={()=>{}}
            /> Check All

            { this.state.list_womenShort.map((list_womenShort) => {
                return (<CheckBox handleCheckChieldElement={this.handleChecked}  {...list_womenShort} />)
            })}

            <button type="submit" onClick={this.handleSubmit}>save</button>
        </div>
        );
    }
}
export default Hairstyles;
