import React from 'react';
import emailIcon from './pic/email_icon.png';
import passwordIcon from './pic/password_icon.png';
import visibleIcon from './pic/visible_icon.png';
import invisibleIcon from './pic/invisible_icon.png';


class Login extends React.Component {
    constructor()
    {
        super();
        this.state = { hidePassword: true }
    }
    managePasswordVisibility = () =>
    {
        this.setState({ hidePassword: !this.state.hidePassword });
    }

    componentDidUpdate(){
        console.log(this.state.hidePassword)
    }

    render(){
        return(
            <form className = "LoginForm" >

                <h1>Sign in</h1>
                <h3>to comtinuous your service</h3>

                <div className="Form">
                    <img src={emailIcon} width="50" />
                    <input type="email" placeholder="Email" />
                </div>

                <div className="Form">
                    <img src={passwordIcon} width="50" />
                    <input type={this.state.hidePassword? "password": "text"} placeholder="Password" />
                    <img onClick={this.managePasswordVisibility} src={this.state.hidePassword? invisibleIcon: visibleIcon} width="30"/>
                    
                </div>
                <button className="Button" type="submit">Sing in</button>

                <h5>Not a member ?</h5>
                <a>Sing up</a>
            </form>
            
        );
    }
}
export default Login;
