    import React from "react";

    export const CheckBox = props => {
        return (
          <li>
           <input key={props.id} onClick={props.handleCheckChieldElement} type="checkbox" checked={props.isChecked} value={props.value} /> {props.value}
          </li>
        )
    }

    class Hairstyles extends React.Component {
        constructor(props) {
            super(props)
            this.state = {
            fruites: [
                {id: 1, value: "banana", isChecked: false},
                {id: 2, value: "apple", isChecked: false},
                {id: 3, value: "mango", isChecked: false},
                {id: 4, value: "grap", isChecked: false}
            ]
            }
        }
        
        handleAllChecked = (event) => {
            let fruites = this.state.fruites
            fruites.forEach(fruite => fruite.isChecked = event.target.checked) 
            this.setState({fruites: fruites})
        }

        handleCheckChieldElement = (event) => {
            let fruites = this.state.fruites
            fruites.forEach(fruite => {
            if (fruite.value === event.target.value)
                fruite.isChecked =  event.target.checked
            })
            this.setState({fruites: fruites})
            console.log(this.state)
        }

        render() {
            return (
            <div className="App">
            <h1> Check and Uncheck All Example </h1>
            <input type="checkbox" onClick={this.handleAllChecked}  value="checkedall" /> Check / Uncheck All
                <ul>
                {
                this.state.fruites.map((fruite) => {
                    return (<CheckBox handleCheckChieldElement={this.handleCheckChieldElement}  {...fruite} />)
                })
                }
                </ul>
            </div>
            );
        }
        }
export default Hairstyles;
