import React from "react";

export const CheckBox = props => {
    return (
        <div>
            <input 
                key = {props.id} 
                onClick = {props.handleCheckChieldElement} 
                type = "checkbox" 
                checked = {props.isChecked} 
                value = {props.value} 
                onChange={props.checkboxHandler} 
            /> 
            {props.value}
        </div>
    )
}

class Hairstyles extends React.Component {
    constructor() {
        super()
        this.state = {
            womenShort: [],
            womenMedium: [],
            womenLong: [],
            menShort: [],
            menLong: [],

            list_womenShort: [
                {id: 1, value: "banana", key: 1,isChecked: false},
                {id: 2, value: "apple",  key: 2,isChecked: false},
                {id: 3, value: "mango",  key: 3,isChecked: false},
                {id: 4, value: "grap",  key: 4,isChecked: false}
            ]
        }
    }
    
    handleAllChecked = event => {
        this.state.list_womenShort.forEach(list_womenShort => list_womenShort.isChecked = event.target.checked) 
        this.setState({list_womenShort: this.state.list_womenShort})
    }

    handleCheckChieldElement = event => {
        this.state.list_womenShort.forEach(list_womenShort => {
        if (list_womenShort.value === event.target.value)
            list_womenShort.isChecked =  event.target.checked
        })
        this.setState({list_womenShort: this.state.list_womenShort})
        console.log(this.state)
    }
    handleSubmit = event => {
        event.preventDefault();
        this.state.list_womenShort.forEach(list_womenShort => {
            if(list_womenShort.isChecked){
                this.state.box.push(list_womenShort.value)
            }
        })
        console.log(this.state.box)
    }
    checkboxHandler=e=> {
        this.setState({
          checked: e.target.checked
        });
      }

    render() {
        return (
        <div >
            <input type="checkbox" onClick={this.handleAllChecked}  onChange={this.checkboxHandler} value="checkedall" /> Check All

            { this.state.list_womenShort.map((list_womenShort) => {
                return (<CheckBox handleCheckChieldElement={this.handleCheckChieldElement}  {...list_womenShort} />)
            })}
            <button type="submit" onClick={this.handleSubmit}>save</button>
        </div>
        );
    }
}
export default Hairstyles;
