import React from 'react';
import emailIcon from './pic/email_icon.png';
import passwordIcon from './pic/password_icon.png';
// import visibleIcon from './pic/visible_icon.png';
// import invisibleIcon from './pic/invisible_icon.png';
// import errorIcon from './pic/error_icon.png';

class SignUp extends React.Component {
    constructor()
    {
        super();
        this.state = { 
            isCustomer : false,
            isShop :false,
            userType : ""
        }
    }
    selectCustomer = () => {
        this.setState({ 
            isShop: false, isCustomer: true, userType : "customer type"
        });
    }
    selectShop = () => {
        this.setState({ 
            isShop: true, isCustomer: false, userType: "shop type"
        });
    }
    handleSubmit = event => {
        event.preventDefault();
        if(this.state.isCustomer || this.state.isShop){
            console.log(this.state);
            this.setState(this.state);
        }
    };

    render(){
        return(
            <div className="big_container">
                <div className="container_signup">
                    <div className="wrap_signup">
                        <form className="signup_form" onSubmit={this.handleSubmit}>

                            <span className="signup_form_title">
                                <h1 style={{color:"#CB2D6F",fontSize:"50px"}}>
                                    Sign up
                                </h1>
                            </span>

                            <div className="container_type">
                                <div 
                                    className="wrap_type" 
                                    id = "userType"
                                    value = "customer type"
                                    onClick = {this.selectCustomer} >
                                    <img 
                                        src = {emailIcon} 
                                        alt = "customer type" 
                                        width="100px"
                                    />
                                </div>
            
                                <div 
                                    className="wrap_type" 
                                    id = "userType"
                                    value = "shop type"
                                    onClick = {this.selectShop} >
                                    <img 
                                        src = {passwordIcon} 
                                        alt = "shop type"
                                        width ="100px"
                                    />
                                </div>
                            </div>
                            <div className="container_next_bt">
                                <div>
                                    <button className="login_button">
                                            Back
                                    </button>
                                </div>
                                <div>
                                    <button className="login_button">
                                            Next
                                    </button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}
export default SignUp;
