import React from 'react';
import userIcon from './pic/user_icon.png'
import passwordIcon from './pic/password_icon.png';
import errorIcon from './pic/error_icon.png';

class SignUpForCustomerTwo extends React.Component {
    constructor()
    {
        super();
        this.state = { 
            // porfilePic: {userIcon},
            password: "",
            confirmPassword: "",
            passwordError: "",
            confirmPasswordError: ""
        }
    }

    // selectProfilepic = event => {
    //     this.setState({ profilePic: event.target.files[0]})
    // }
    
    validate = () => {
        var pattern = new RegExp(/^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).*$/);
        let passwordError=""
        let confirmPasswordError=""
        
        if(!this.state.password){
            passwordError = "invalid password !"
        }
        else if (!pattern.test(this.state.password)) {
            passwordError = "Must match pattern";
        }
        if(!this.state.confirmPassword){
            confirmPasswordError = "invalid password !"
        }
        else if(this.state.password !== this.state.confirmPassword){
            confirmPasswordError = "Not match , please try again."
        }

        if(confirmPasswordError || passwordError){
            this.setState({ passwordError, confirmPasswordError });
            return false;
        }
        return true;
    };

    
    handleChange = event => {
        // event.preventDefault();
        this.setState({[event.target.id]: event.target.value});
    };

    handleSubmit = event => {
        event.preventDefault();
        const isValid = this.validate();
        // console.log(this.state);
        if (isValid) {
          console.log(this.state);
          this.setState(this.state);
        }
    };

    render(){
        return(
            <div className="big_container">
                <div className="container_signup">
                    <div className="wrap_signup">
                        <div className="signup_form">

                            <span className="signup_form_title">
                                <h1 style={{color:"#CB2D6F",fontSize:"50px"}}>
                                    Sign in
                                </h1>
                                <h5 style={{color:"#CB2D6F"}}>
                                    Page 2 of 2
                                </h5>
                            </span>
                            <div className = "bigcontainer_input">
                                <div className = "container_iput">
                                    
                                    <div className="wrap_profile">
                                        <div className="upload_profile">
                                            <ImageUpload />
                                            {/* <input 
                                                className = "upload_p"
                                                type="file" 
                                                id = "profile_pic"
                                                value = {this.state.porfilePic}
                                                onChange = {this.selectProfilepic}
                                            />    
                                            <img src={this.state.profilePic} alt=""/> */}
                                        </div>
                                    </div>    

                                    <div className="pattern">
                                        <p>Password must be at least 8 characters<br />contain 1 or more uppercase, lowercase and number</p>
                                    </div>
                                    
                                    <div className="wrap_input">
                                        <img className="input_icon"src={passwordIcon} alt=""/>
                                        <input  
                                            className = "input" 
                                            type = "password"
                                            id = "password"
                                            placeholder = "Password"
                                            maxLength = "16"
                                            value = {this.state.password}  
                                            onChange = {this.handleChange} 
                                        />
                                        <div className={this.state.passwordError===""? "validate_wrap" :"invalidate_wrap"}>
                                            <div className="erroricon">
                                                <img src={errorIcon} alt= "" width="20px" />
                                            </div>
                                            <div className="texterror">
                                                <span>{this.state.passwordError}</span>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="wrap_input">
                                        <img className="input_icon"src={passwordIcon} alt=""/>
                                        <input  
                                            className = "input" 
                                            type = "password"
                                            id = "confirmPassword"
                                            placeholder = "Confirm password"
                                            value = {this.state.confirmPassword}  
                                            onChange = {this.handleChange} 
                                        />
                                        <div className={this.state.confirmPasswordError===""? "validate_wrap" :"invalidate_wrap"}>
                                            <div className="erroricon">
                                                <img src={errorIcon} alt= "" width="20px" />
                                            </div>
                                            <div className="texterror">
                                                <span>{this.state.confirmPasswordError}</span>
                                            </div>
                                        </div>
                                    </div>
                    
                                </div>
                            </div>
                            <div className="container_next_bt">
                                <div>
                                    <button className="login_button">
                                        Back
                                    </button>
                                </div>
                                <form onSubmit={this.handleSubmit}>
                                    <button className="login_button" >
                                        Submit
                                    </button>
                                </form>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
// export default SignUpForCustomerTwo;

export class ImageUpload extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        file: "",
        imagePreviewUrl: {userIcon}
    };
  }

  _handleSubmit(e) {
    e.preventDefault();
    // TODO: do something with -> this.state.file
    console.log('handle uploading-', this.state.file);
  }

  _handleImageChange(e) {
    e.preventDefault();

    let reader = new FileReader();
    // let file = 

    reader.onloadend = () => {
      this.setState({
        file: e.target.files[0] ,
        imagePreviewUrl: reader.result
      });
    }

    reader.readAsDataURL(e.target.files[0])
  }

  render() {
    let {imagePreviewUrl} = this.state;
    let $imagePreview = null;
    if (imagePreviewUrl) {
      $imagePreview = (<img src={imagePreviewUrl} />);
    } else {
      $imagePreview = (<div className="previewText">Please select an Image for Preview</div>);
    }

    return (
      <div className="previewComponent">
        <form onSubmit={(e)=>this._handleSubmit(e)}>
          <input className="fileInput" 
            type="file" 
            onChange={(e)=>this._handleImageChange(e)} />
          <button className="submitButton" 
            type="submit" 
            onClick={(e)=>this._handleSubmit(e)}>Upload Image</button>
        </form>
        <div className="imgPreview">
          {$imagePreview}
        </div>
      </div>
    )
  }
}
  
// ReactDOM.render(<ImageUpload/>, document.getElementById("mainApp"));
