    import React from "react";

    class Checkbox extends React.Component {
        // static defaultProps = {
        //     checked: false
        // }
        render() {
            return (
                <input
                    type={this.props.type}
                    name={this.props.name}
                    checked={this.props.checked}
                    onChange={this.props.onChange}
                />
            );
        }
    }

    const checkboxes = [
        {
            name: "check-box-1",
            key: "checkBox1",
            label: "Check Box 1"
        },
        {
            name: "check-box-2",
            key: "checkBox2",
            label: "Check Box 2"
        },
        {
            name: "check-box-3",
            key: "checkBox3",
            label: "Check Box 3"
        },
        {
            name: "check-box-4",
            key: "checkBox4",
            label: "Check Box 4"
        }
    ];

    class Hairstyles extends React.Component {
        constructor() {
            super();

            this.state = {
                checkedItems: []
            };

            // this.handleChange = this.handleChange.bind(this);
        }

        handleChange = e => {
            const item = e.target.name;
            const isChecked = e.target.checked;
            this.setState(prevState => ({
                checkedItems: prevState.checkedItems.set(item, isChecked)
            }));
        };

        deleteCheckboxState = (name, checked) => {
            const updateChecked = checked == null ? true : false;
            this.setState(prevState => prevState.checkedItems.set(name, updateChecked));
        };

        clearAllCheckboxes = () => {
            const clearCheckedItems = new Map();
            this.setState({ checkedItems: clearCheckedItems });
        };

        render() {
            

            // const checkboxesToRender = checkboxes.map(item => {
            //     return (
            //         <label key={item.key}>
            //             {item.name}
            //             <Checkbox
            //                 name={item.name}
            //                 checked={this.state.checkedItems.get(item.name) || false}
            //                 onChange={this.handleChange}
            //                 type="checkbox"
            //             />
            //         </label>
            //     );
            // });

            // const checkboxesDeleteHandlers = checkboxes.map(item => {
            //     return (
            //         <span
            //             key={item.name}
            //             onClick={() =>
            //                 this.deleteCheckboxState(
            //                     item.name,
            //                     this.state.checkedItems.get(item.name)
            //                 )
            //             }
            //         >
            //             {item.name} &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
            //         </span>
            //     );
            // });

            return (
                <div >
                    <Checkbox
                            name={this.name}
                            checked={this.state.checkedItems.push(this.name) || false}
                            onChange={this.handleChange}
                            type="checkbox"
                        />
                    {/* {checkboxesToRender}
                    <br /> {checkboxesDeleteHandlers}
                    <p onClick={this.clearAllCheckboxes}>clear all</p> */}
                </div>
            );
        }
    }
export default Hairstyles;
