import React from 'react';
import userIcon from './pic/user_icon.png'

class ImageUpload extends React.Component {
    constructor() {
      super();
      this.state = {
        imageFile: "",
        imagePreview: {userIcon}
      };
    }
  
    handleSubmit = event => {
      event.preventDefault();
      console.log(this.state.imageFile);
    }
  
    handleChange = event => {
      event.preventDefault();
  
      let reader = new FileReader();
      // let file = event.target.files[0];
  
      reader.onloadend = () => {
        this.setState({
          imageFile: event.target.files[0],
          imagePreview: reader.result
        });
      }
  
      reader.readAsDataURL(event.target.files[0])
    }
  
    render() {
      // let {imagePreviewUrl} = this.state;
      let preview = null;
      if (this.state.imagePreview) {
        preview = (<img src={this.state.imagePreview} alt=""/>);
      } 
      else {
        preview = (<div className="previewText">Please select an Image</div>);
      }
  
      return (
        <div className="wrap_input">
          <form onSubmit={this.handleSubmit}>
            <input className="input" 
              type="file" 
              onChange={this.handleChange} />
            <div className="wrap_button">
              <button className="login_button" type="submit" onClick={this.handleSubmit}>
                  Upload Image
              </button>
            </div>
          </form>
          <div className="imagePreview" style={{width: "100px"}}>
            {preview}
          </div>
        </div>
      )
    }
}
export default ImageUpload;
