import React from 'react'
import userImage from './pic/user_green_icon.png'

class ImageUpload extends React.Component {
    constructor(){
        super();
        this.state = {
            imageFile: "",
            imagePreview: "",
            imageUrl: "",
            isSaved: false
        }
    }
    showSave = () => {
        this.setState({ isSaved: true })
    }
    
    hideSave = () => {
        this.setState({ isSaved: false})
    }

    handleSubmit = event => {
        event.preventDefault()
        if(isSaved){
            console.log(this.state)
            this.props.getFile(this.state.imageFile, this.state.imagePreview, this.state.imageUrl )
        }
    }

    handleChange = event => {
        event.preventDefault();

        let reader = new FileReader();
        let file = event.target.files[0];

        reader.onloadend = () => {
            this.setState({
                imageFile: file, imagePreview: reader.result, imageUrl: URL.createObjectURL(file)
            });
        }
        reader.readAsDataURL(file);
        console.log(this.state)
        
    }

    render(){
        return(
            <div className = "container_profile">
                
                <div className = "wrap_preview">
                    <img 
                        className = "image_preview"
                        alt = ""
                        // value = {this.state.imagePreview}
                        src = {this.state.imagePreview? this.state.imagePreview : userImage }
                    />
                </div>

                <form onSubmit={this.handleSubmit}>
                    <input 
                        className = "file_input"
                        type = "file"
                        id = "image_file"
                        onChange = {this.handleChange}
                        onClick ={this.showSave}
                    />
                   <div className="wrap_button">
                        <button className={this.state.isSaved? "showsave_button": "hidsave_button"} type="submit" onClick={this.hideSave}>
                            Save
                        </button>
                    </div>
                </form>

            </div>
        )
    }
}

export default ImageUpload;
