import React from 'react';
import emailIcon from './pic/email_icon.png';
import passwordIcon from './pic/password_icon.png';
import visibleIcon from './pic/visible_icon.png';
import invisibleIcon from './pic/invisible_icon.png';

const emailRegex = RegExp(
    /^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);
  
class Login extends React.Component {
    constructor()
    {
        super();
        this.state = { 
            hidePassword: true ,
            email: "",
            password: "",
            emailError: "",
            passwordError: ""
        }
    }
    
    validate = () => {
        // let nameError = "";
        let emailError = "";
        let passwordError = "";
        
        if (!this.state.email.includes("@") || !this.state.email || !emailRegex.test(value)) {
            emailError = "invalid email";
        }
        if (!this.state.password) {
            passwordError = "invalid password";
        }
        if (emailError || passwordError) {
            this.setState({ emailError, passwordError });
            return false;
        }

        return true;
    };

    handleChange = event => {
        this.setState({[event.target.name]: event.target.value});
    };

    handleSubmit = event => {
        event.preventDefault();
        const isValid = this.validate();
        if (isValid) {
          console.log(this.state);
          this.setState(email, password, emailError, passwordError);
        }
    };
    
    managePasswordVisibility = () =>
    {
        this.setState({ hidePassword: !this.state.hidePassword });
    }

    componentDidUpdate(){
        console.log(this.state.hidePassword)
    }

    render(){
        return(
            <div className="big_container">
                <div className="container_login">
                    <div className="wrap_login">
                        <form className="login_form" onSubmit={this.handleSubmit}>

                            <span className="login_form_title">
                                <h1 style={{color:"white"}}>
                                    Sign in
                                </h1>
                                <h5 style={{color:"white"}}>
                                    to continuous your service
                                </h5>
                            </span>
        
                            <div className="wrap_input">
                                <img src={emailIcon} width="35" />
                                <input  
                                    className = "input" 
                                    type = "email"
                                    name = "email"
                                    id = "email"
                                    placeholder = "Email" 
                                    value = {this.state.email}  
                                    onChange = {this.handleChange} 
                                />
                                <div style={{ fontSize: 12, color: "red" }}>
                                    {this.state.emailError}
                                </div>
                            </div>
        
                            <div className="wrap_input" >
                                <img src = {passwordIcon} width = "35" />
                                <input 
                                    className="input"
                                    type = {this.state.hidePassword? "password": "text"} 
                                    name = "password"
                                    id = "password"
                                    placeholder = "Password" 
                                    value = {this.state.password}  
                                    onChange = {this.handleChange} 
                                />
                                <img 
                                    onClick={this.managePasswordVisibility} 
                                    src={this.state.hidePassword? visibleIcon: invisibleIcon} 
                                    width="30" 
                                />
                                <div style={{ fontSize: 12, color: "red" }}>
                                    {this.state.passwordError}
                                </div>
                            </div>
        
                            <div className="wrap_button">
                                <button className="login_button">
                                    Sign in
                                </button>
                            </div>

                            <h5 style={{color:"white"}}>Or</h5>

                            <div className="wrap_button">
                                <button className="login_button">
                                    Sign up
                                </button>
                            </div>

                        </form>
                    </div>
                </div>
            </div>
        );
    }
}
export default Login;
