import React from 'react'
import userIcon from './pic/user_icon.png'

class ImageUpload extends React.Component {
    constructor() {
      super();
      this.state = {
        imageFile: {userIcon},
        imagePreview: ""
      };
    }
  
    handleSubmit = event => {
      event.preventDefault();
      console.log(this.state);
      this.setState(this.state);
    }
  
    handleChange = event => {
      event.preventDefault();
  
      let reader = new FileReader();
      let file = event.target.files[0];
  
      reader.onloadend = () => {
        this.setState({
          imageFile: file, imagePreview: reader.result
        });
      }
  
      reader.readAsDataURL(file)
    }
  
    render() {
      return (
        <div className="container_imgupload">
          <form onSubmit={this.handleSubmit}>
            <input className="fileInput" 
              type="file" 
              id="imageFile"
              onChange={this.handleChange} />
            <button className="submitButton" 
              type="submit" 
              onClick={this.handleSubmit}>Upload Image</button>
          </form>
          <div className="wrap_imgpreview">
            <img 
              className="img_preview" 
              alt=""
              src = {this.state.imagePreview ? this.state.imagePreview : userIcon }
            />
          </div>
        </div>
      )
    }
}
export default ImageUpload; 
