import React from 'react'
import userImage from './pic/user_green_icon.png'

class MultipleImageUpload extends React.Component {
    constructor(){
        super();
        this.state = {
            imageFile: [],
            imagePreview: [],
            imageUrl: [],
            isSaved: false
        }
    }
    
    hideSaved = () => {
        this.setState({ isSaved: false})
    }

    handleSubmit = event => {
        event.preventDefault()
        console.log(this.state)
        // if(!this.state.isSaved){
        //     this.props.getFile(this.state.imageFile, this.state.imagePreview, this.state.imageUrl )
        // }
    }

    handleChange = event => {
        event.preventDefault();

        // let reader = new FileReader();
        // let file = event.target.files[0];
        

        // reader.onloadend = () => {
        //     this.setState({
        //         imageFile: file, imagePreview: reader.result, imageUrl: URL.createObjectURL(file)
        //     });
        // }
        // this.setState({ isSaved: true })
        // if (file && file.type.match('image.*')) {
        //     reader.readAsDataURL(file);
        // }
        // console.log(this.state)

        this.imagePreview.push(e.target.files)
        for (let i = 0; i < this.state.imagePreview[0].length; i++) {
            this.imageUrl.push(URL.createObjectURL(this.imagePreview[0][i]))
        }
        this.setState({ imageFile: this.imagePreview })
    }

    render(){
        return(
            <div className = "container_profile">
                
                <div className = "wrap_preview">
                    {(this.imagePreview || []).map(url => (
                        <img src={url} alt="..." />
                    ))}
                    {/* <img 
                        className = "image_preview"
                        alt = ""
                        src = {this.state.imagePreview? this.state.imagePreview : this.props.imagePreview }
                    /> */}
                </div>

                <form onSubmit={this.handleSubmit}>
                    <input 
                        className = "file_input"
                        type = "file"
                        id = "image_file"
                        multiple onChange = {this.handleChange}
                    />
                   <div className="wrap_button">
                        <button className={this.state.isSaved? "showsave_button": "hidsave_button"} type="submit" onClick={this.hideSaved}>
                            Save
                        </button>
                    </div>
                </form>

            </div>
        )
    }
}

export default MultipleImageUpload;
