import React from 'react'

class ImageUpload extends React.Component {
    constructor() {
      super();
      this.state = {
        imageFile: "",
        imagePreview: ""};
    }
  
    handleSubmit = event => {
      event.preventDefault();
      console.log(this.state);
      this.setState(this.state);
    }
  
    handleChange = event => {
      event.preventDefault();
  
      let reader = new FileReader();
      let file = event.target.files[0];
  
      reader.onloadend = () => {
        this.setState({
          imageFile: file, imagePreview: reader.result
        });
      }
  
      reader.readAsDataURL(file)
    }
  
    render() {
      // let {imagePreview} = this.state;
      let previewArea = null;

      if (this.state.imagePreview) {
        preview = (<img src={this.state.imagePreview} />);
      } else {
        preview = (<div className="preview_area"><img src={userIcon} alt="" /></div>);
      }
  
      return (
        <div className="container_imgupload">
          <form onSubmit={this.handleSubmit}>
            <input className="fileInput" 
              type="file" 
              onChange={this.handleChange} />
            <button className="submitButton" 
              type="submit" 
              onClick={this.handleSubmit}>Upload Image</button>
          </form>
          {/* <div className="imgPreview">
            {preview}
          </div> */}
        </div>
      )
    }
}
export default ImageUpload; 
