import React from 'react';
import { Link } from 'react-router-dom';
import userIcon from './pic/user_icon.png';
import Time from 'react-simple-timefield';

//ที่อยู่ เวลา รูปบรรยากาศ
class ShopInformation extends React.Component {
    constructor()
    {
        super();
        this.state = { 
            address: "",
            openhours: "09:00",
            closehours: "18:00"
        }
        this.timeChange = this.timeChange.bind(this);
    }

    timeChange(time) {
        const setTime = time.replace(/-/g, ':');
        const openhours = setTime.substr(0, 5);
        this.setState({openhours : openhours});
      }
    
    handleChange = event => {
        // event.preventDefault();
        this.setState({[event.target.id]: event.target.value});
    };

    handleSubmit = event => {
        event.preventDefault();
        // console.log(this.state);
        // if (isValid) {
        //   console.log(this.state);
        //   this.setState(this.state);
        //   this.props.history.push('/signup_shop_2')
        // }
    };

    render(){
        return(
            <div className="big_container">
                {/* <div className="container_signup"> */}
                    <div className="wrap_info">

                        <div className = "title">
                            <h1 style={{color:"#CB2D6F",fontSize:"50px"}}>
                                Shop Infomation
                            </h1>
                        </div>
                        
                        <div className="signup_form">

                            <div className = "bigcontainer_input">
                                <div className="wrap_input_info">
                                    <img className="input_icon"src={userIcon} alt=""/>
                                    <input  
                                        className = "input_info" 
                                        type = "text"
                                        id = "address"
                                        placeholder = "Address"
                                        value = {this.state.address}                                              
                                        onChange = {this.handleChange} 
                                    />
                                </div>
                                
                                <div className="wrap_input_info">
                                    <img className="input_icon"src={userIcon} alt=""/>
                                    {/* <input  
                                        // className = "input_info" 
                                        type = "time"
                                        id = "openhours"
                                        placeholder = "Open hours"
                                        value = {this.state.openhours}                                              
                                        onChange = {this.handleChange} 
                                    /> */}
                                    <div>
                                    <Time
                                        className = "input_info"
                                        value={this.state.openhours}
                                        onChange={this.timeChange}
                                        style={{
                                            border: '2px solid #666',
                                            fontSize: 42,
                                            width: 107,
                                            padding: '5px 8px',
                                            color: '#333',
                                            borderRadius: 3
                                        }}
                                    />
                                    </div>
                                </div>
                                
                            </div>
                            
                            <div className="container_next_bt">
                                <Link className="link" to="/signup">
                                    <div>
                                        <button className="login_button" type="reset">
                                            Back
                                        </button>
                                    </div>
                                </Link>
                                <form onSubmit={this.handleSubmit} >
                                    <button className="login_button" type="submit" onClick={this.handleSubmit}>
                                        Next
                                    </button>
                                </form>
                            </div>

                        </div>
                    </div>
                {/* </div> */}
            </div>
        );
    }
}
export default ShopInformation;
