import React from 'react';
import emailIcon from './pic/email_icon.png';
import passwordIcon from './pic/password_icon.png';
// import visibleIcon from './pic/visible_icon.png';
// import invisibleIcon from './pic/invisible_icon.png';
// import errorIcon from './pic/error_icon.png';

class SignUp extends React.Component {
    constructor()
    {
        super();
        this.state = { 
            selectType : false,
            userType : "",
            costomerType : "costomer type",
            shopType : "shop type"
        }
    }
    selectType = () => {
        this.setState({ 
            selectType: !this.state.selectType 
            // userType : this.state.userType
        });
    }
    handleChange = event => {
        // event.preventDefault();
        // this.setState({ [event.target.id]: event.target.value });
        this.setState({userType : event.target.value});
    };

    handleSubmit = event => {
        event.preventDefault();
        if(this.state.selectType){
            console.log(this.state);
            this.setState(this.state);
        }
    };

    render(){
        return(
            <div className="big_container">
                <div className="container_signup">
                    <div className="wrap_signup">
                        <form className="signup_form" onSubmit={this.handleSubmit}>

                            <span className="signup_form_title">
                                <h1 style={{color:"#CB2D6F",fontSize:"50px"}}>
                                    Sign up
                                </h1>
                            </span>
        
                            <div className="wrap_type">
                                <img 
                                    className = "user_type" 
                                    id = "userType"
                                    src = {emailIcon} 
                                    alt = "customer type" 
                                    value = "customer type"
                                    onClick = {this.selectType}
                                    onChange = {this.handleChange}
                                    width="100px"
                                />
                            </div>
        
                            <div className="wrap_type" >
                                <img 
                                    className = "user_type"
                                    id = "userType"
                                    src = {passwordIcon} 
                                    alt = "shop type"
                                    value = "shop type"
                                    onClick = {this.selectType}
                                    onChange = {this.handleChange}
                                    width ="100px"
                                />
                            </div>
        
                            <div className="wrap_button">
                                <button className="login_button">
                                    Next
                                </button>
                            </div>

                        </form>
                    </div>
                </div>
            </div>
        );
    }
}
export default SignUp;
