import React from 'react';
import { Link } from 'react-router-dom';
import shopIcon from './pic/shop_icon.png';
import timeIcon from './pic/clock_icon.png'
import MultipleImageUpload from './MultipleImageUpload';

//ที่อยู่ เวลา รูปบรรยากาศ
class ShopInformation extends React.Component {
    constructor()
    {
        super();
        this.state = { 
            womenShort: [
                {id: 1, value: "kk", isChecked: false},
                {id: 2, value: "banana", isChecked: false},
                {id: 3, value: "banana", isChecked: false},
                ],
            womenMedium: [],
            womenLong: [],
            menShort: [],
            menLong: []
        }
        this.getFile = this.getFile.bind(this);
    }

    getFile(img_file, img_preview, img_url) {
        this.setState({
            imageFile: img_file, imagePreview: img_preview, imageUrl: img_url
        });
    }

    handleAllChecked = (event) => {
        this.state.womenLong.forEach(womenLong => womenLong.isChecked = event.target.checked) 
        this.setState({womenLong: this.state.womenLong})
    }
    
    handleCheckChieldElement = (event) => {
        this.state.womenLong.forEach(womenLong => {
            if (womenLong.value === event.target.value){
                womenLong.isChecked =  event.target.checked
            }
           
        })
        this.setState({womenLong: this.state.womenLong})
    }

/*
    handleChange = event => {
        // event.preventDefault();
        this.setState({[event.target.id]: event.target.value});
    };

    handleSubmit = event => {
        event.preventDefault();
        // console.log(this.state);
        // if (isValid) {
          console.log(this.state);
          this.setState(this.state);
        //   this.props.history.push('/signup_shop_2')
        // }
    };
*/
    render(){
        return(
            <div className="big_container">
                {/* <div className="container_signup"> */}
                    <div className="wrap_info">

                        <div className = "title">
                            <h1 style={{color:"#CB2D6F",fontSize:"30px"}}>
                                Shop Infomation
                            </h1>
                            <h5 style={{color:"white",fontSize:"20px"}}>
                                Hairstyles
                            </h5>
                        </div>
                        
                        <div className="signup_form">

                            <div className = "bigcontainer_info">
                                <div className = "line_info">
                                    <div style={{width:"100px"}}>
                                        <p style={{color:"white", marginRight:"20px"}}>Women</p>
                                    </div>
                                    <div className="wrap_input_info">
                                        <input type="checkbox" onClick={this.handleAllChecked}  value="checkedall" /> Check all
                                        <ul>
                                            {
                                                this.state.fruites.map((fruite) => {
                                                    return (<CheckBox handleCheckChieldElement={this.handleCheckChieldElement}  {...fruite} />)
                                                })
                                            }
                                        </ul>
                                    </div>
                                </div>
                            
                                <div className = "line_info">
                                    
                                </div>
                                
                            </div>
                            
                            <div className="container_right_bt">
                                <Link className="link" >
                                    <div>
                                        <button className="login_button" type="reset">
                                            Back
                                        </button>
                                    </div>
                                </Link>
                                <form onSubmit={this.handleSubmit} >
                                    <button className="login_button" type="submit" onClick={this.handleSubmit}>
                                        Next
                                    </button>
                                </form>
                            </div>

                        </div>
                    </div>
                {/* </div> */}
            </div>
        );
    }
}
export default ShopInformation;

export const CheckBox = props => {
    return (
      <li>
       <input key={props.id} onClick={props.handleCheckChieldElement} type="checkbox" checked={props.isChecked} value={props.value} /> {props.value}
      </li>
    )
}
