import React from 'react'
import userImage from './pic/user_green_icon.png'

class ImageUpload extends React.Component {
    constructor(){
        super();
        this.state = {
            imageFile: "",
            imagePreview: ""
        }
    }
    
    handleSubmit = event => {
        event.preventDefault()
        // console.log(this.state)
        this.props.getFile(this.state.imageFile,this.state.imagePreview)
    }

    handleChange = event => {
        // event.preventDefault();

        // let reader = new FileReader();
        // let file = event.target.files[0];

        // reader.onloadend = () => {
        //     this.setState({
        //         imageFile: file, imagePreview: reader.result
        //     });
        // }
        // reader.readAsDataURL(file);
        if (event.target.files && event.target.files[0]) {
          let file = event.target.files[0];
          this.setState({
            imageFile: URL.createObjectURL(file)
          });
        }
    }

    render(){
        return(
            <div className = "container_profile">
                
                <div className = "wrap_preview">
                    <img 
                        className = "image_preview"
                        alt = ""
                        // value = {this.state.imagePreview}
                        // src = {this.state.imagePreview? this.state.imagePreview : userImage }
                        src = {this.state.imageFile? this.state.imageFile: userImage }
                    />
                </div>

                <form onSubmit={this.handleSubmit}>
                    <input 
                        className = "file_input"
                        type = "file"
                        id = "image_file"
                        // value = {this.state.imageFile}
                        onChange = {this.handleChange}
                        // onClick = {this.handleSubmit}
                    />
                   <div className="wrap_button">
                        <button className="login_button" type="submit">
                            Save
                        </button>
                    </div>
                </form>

            </div>
        )
    }
}

export default ImageUpload;
